    def Clock(self):
        time = datetime.now()
        formatted_time = time.strftime("%A %d/%m/%Y  %H:%M:%S %p")
        self.DateTime.setText(formatted_time)
        formatted_time_2 = time.strftime("%H:%M:%S")
        self.lcdNumber.setDigitCount(8)
        self.lcdNumber.display(formatted_time_2)
        formatted_time_3 = time.strftime("%A %d/%m/%Y")
        self.Date.setText(formatted_time_3)

    def NightDayShow(self):
        time = datetime.now()
        self.DayNightPhoto = QtWidgets.QLabel(self.Home)
        self.DayNightPhoto.setGeometry(QtCore.QRect(55, 140, 120, 100))
        self.DayNightPhoto.setObjectName("DayNightPhoto")
        formatted_time = time.strftime("%H")
        self.DayNightPhoto.setScaledContents(True)
        if int(formatted_time) >= 8 | int(formatted_time) <= 5:
            self.DayNightPhoto.setPixmap(QtGui.QPixmap("Night.jpg"))
        else:
            self.DayNightPhoto.setPixmap(QtGui.QPixmap("Day.png"))
        
    def Weather(self):
        try:
            s = HTMLSession()

            query = 'Weston-Super-Mare'
            url = f'https://www.google.com/search?q=weather+{query}'

            r = s.get(url, headers={'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/101.0.4951.64 Safari/537.36 Edg/101.0.1210.53'})

            temp = (r.html.find('span#wob_tm', first = True).text)
            unit = (r.html.find('div.vk_bk.wob-unit span.wob_t', first = True).text)
            Weather = (r.html.find('span#wob_dc', first = True).text)
            self.label_2.setText(temp + unit + " | " + Weather)
        except:
            pass

    def HomePageInventory(self):

        inventory = dataBase('inventory.txt')
        inventory.readRecords()
        dictData = inventory.outData()
        contents = ''
        count = 0
        for dict in dictData['inventory']:
            contents = contents + json.dumps(dict) + "\n"
            count += 1
        line = 30 + 19 * (count + 1)
        contents = re.sub('[{"}]', '', contents)
        self.FrontPageInvent.setText(contents)
        self.FrontPageInvent.setMinimumSize(QSize(850, line))
        self.verticalLayout_2.addWidget(self.FrontPageInvent)
        self.scrollArea_2.setWidget(self.scrollAreaWidgetContents_2)


    def InventoryPageInventory(self):
        inventory = dataBase('inventory.txt')
        inventory.readRecords()
        dictData = inventory.outData()
        contents = ''
        count = 0
        for dict in dictData['inventory']:
                contents = contents + json.dumps(dict) + "\n"
                count += 1
        line = 30 + 19 * (count + 1)
        contents = re.sub('[{"}]', '', contents)
        self.Inventory_2.setText(contents)
        self.Inventory_2.setMinimumSize(QSize(850, line))
        self.verticalLayout.addWidget(self.Inventory_2)
        self.scrollArea.setWidget(self.scrollAreaWidgetContents)

